  filter(
    `Fiscal Year` == 2023, 
    `Transaction Type Name` == "Obligations",
    !str_detect(`Country Name`, "Region"),
    `Country Name` != "World"
  ) |>
  group_by(`Country Code`, `Country Name`, `Region Name`) |>
  summarize(total_constant_amount = sum(constant_amount)) |>
  arrange(desc(total_constant_amount))
